var VertexForPathSearch = function(id, value) {
	this.id = id;
	this.value = value;
}

//确定邻接表大小，并将二维数组转为邻接表
function setMatrixData(map, map42d) {
	map42d.matrixRow = map.length;
	map42d.matrixCol = map[0].length;
	for (var i = 0, j = 0; i < map42d.matrixRow; i++) {
		map42d.vertexMatrix[i] = [];
		for (var k = 0; k < map42d.matrixCol; k++) {
			map42d.vertexMatrix[i][k] = new VertexForPathSearch(j, map[i][k]) //array2d[i,k]//Math.random()<0.2?1:0
			j++;
		};
	};
	map42d.vertexArray = matrix2Array(map42d.vertexMatrix, map42d)
}

//用来把开始的二维数组转换为 点的邻接表
function matrix2Array(matrix, map42d) {
	var vertexArray = [];
	for (var i = 0, j = 0; i < map42d.matrixRow; i++) {
		for (var k = 0; k < map42d.matrixCol; k++) {
			vertexArray[j] = [];
			if (matrix[i][k].value === 0) {
				k - 1 >= 0 && matrix[i][k - 1].value === 0 && (vertexArray[j].push(matrix[i][k - 1].id));
				i + 1 < map42d.matrixRow && matrix[i + 1][k].value === 0 && (vertexArray[j].push(matrix[i + 1][k].id));
				k + 1 < map42d.matrixCol && matrix[i][k + 1].value === 0 && (vertexArray[j].push(matrix[i][k + 1].id));
				i - 1 >= 0 && matrix[i - 1][k].value === 0 && (vertexArray[j].push(matrix[i - 1][k].id));
			}
			j++
		};
	};
//	console.log(JSON.stringify(vertexArray))
//	console.log("------------")
	return vertexArray;
}
// 转换点座标为id（序号）
var vertex2id = function(x, y, map42d) {
	var id = x * map42d.matrixCol + y;
	return id
}

// 转换id（序号）为座标点
var id2vertex = function(id, map42d) {
	var str = {
		x: Math.floor(id / map42d.matrixCol),
		y: id % map42d.matrixCol
	};
	return str;
}

//搜索两点间的最短路径
var search = function(x1, y1, x2, y2, map42d) {
	var id1 = vertex2id(x1, y1, map42d),
		id2 = vertex2id(x2, y2, map42d),
		queue = [], //需要遍历的队列
		marked = [], //已经访问过的节点序列号
		path = []; //到每个目标点的 所对应的上个目标点
	
	console.log(id1);console.log(id2);
	
	queue.push(id1)
	marked[id1] = true;

	while (queue.length > 0) {
		var v = queue.shift()

		for (var i = 0; i < map42d.vertexArray[v].length; i++) {
			var target = map42d.vertexArray[v][i];
			if (!marked[target]) {
				path[target] = v;
				marked[target] = true;
				queue.push(target);
				if (target === id2) {
					return getPath(id1, id2, path, map42d)
				}
			}
		};
	}
	return false;
}

function getPath(id1, id2, path, map42d) {
	var pathVertexArray = [id2vertex(id2, map42d)]
	while (path[id2] !== id1) {
		pathVertexArray.unshift(id2vertex(path[id2], map42d))
		id2 = path[id2];
	}
	pathVertexArray.unshift(id2vertex(id1, map42d))
	
	return pathVertexArray;
}
